import axios from "axios";
import React from "react";
import { RotatingLines } from "react-loader-spinner";
import { useQuery } from "react-query";
import { useParams } from "react-router-dom";

export default function ProductDetails() {
  const {id} = useParams();
  function getProductDetails() {
    return axios.get(`https://ecommerce.routemisr.com/api/v1/products/${id}`);
  }
  const { isLoading, data } = useQuery("getProductDetails", getProductDetails);
  if (isLoading) {
    return (
      <>
        <div className="vh-100 w-100 d-flex justify-content-center align-items-center bg-black">
          <RotatingLines
            visible={true}
            height="96"
            width="96"
            color="var(--main-color)"
            strokeWidth="5"
            animationDuration="0.75"
            ariaLabel="rotating-lines-loading"
            wrapperStyle={{}}
            wrapperClass=""
          />
        </div>
      </>
    );
  }
  console.log(data.data.data);
  const productData = data.data.data
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-md-4">
            <figure>
              <img
                className="w-100"
                src={productData.imageCover}
                alt={productData.title}
              />
            </figure>
          </div>
          <div className="col-md-8">
            <h1 className=" my-md-3">{productData.title}</h1>
            <h4 className=" my-md-3">{productData.description}</h4>
            <div className="d-flex my-md-3">
            <h4>{productData.category.name}</h4>
            <figure><img className="w-100" style={{height:"50px"}} src={productData.category.image} alt={productData.category.name} /></figure>
            </div>
<div>            {productData.priceAfterDiscount ? (
                      <>
                        <p>
                          <span className="text-decoration-line-through">
                            {productData.price}
                          </span>
                          -{productData.priceAfterDiscount}
                          <span>EGP</span>
                        </p>
                      </>
                    ) : (
                      <p>
                        {productData.price}
                        <span>EGP</span>
                      </p>
                    )}</div>
            <button type="button" className="btn bg-main w-100 text-white">Add Product in Cart + </button>
          </div>
        </div>
      </div>
    </>
  );
}
