import React, { useContext } from "react";
import { addCart } from "../../Context/AddProductToCart";

export default function Cart() {
  const { getProductFromCart ,numberOfCartItem ,totalPrice ,allProducts} = useContext(addCart);
  function getcart() {
    return getProductFromCart();
  }

  return (
    <>
      <div className="container">
        <div className="d-flex justify-content-between align-items-center">
          <h1>Shop cart:</h1>
          <button className="btn btn-outline-success">Delete Cart</button>
        </div>
        <div className="d-flex align-content-center align-items-center">
          <p className="text-main ">Total cart price :</p>
          <p className="text-main">{totalPrice} EGP</p>
        </div>

        <div className="d-flex">
{allProducts.map()}
        </div>
      </div>
    </>
  );
}
