import axios, { Axios } from "axios";
import { useFormik } from "formik";
import React, { useContext } from "react";

export default function Payment() {
  const {cartId} = useContext("addCart")
  const {token} = useContext
  const userData = {
    details: "",
    phone: "",
    city: "",
  };

   async function confirmCachPayment(values) {
    const {data} = await axios.post(`https://ecommerce.routemisr.com/api/v1/orders/${cartId}`, values)
  }
  const myFormik = useFormik({
    initialValues: userData,
    onSubmit: "",
    validate: (values) => {
      const errors = {};
      const phoneRegex = /^01[0125][0-9]{8}$/;
      if (!phoneRegex.test(values.phone)) {
        errors.phone = "Must use Egyptian phone number format";
      }
      if (values.details.length < 12) {
        errors.details = "Must be more than 12 characters";
      }
      return errors;
    },
  });

  return (
    <>
      <div className="container my-2 bg-light">
        <h2>ShippingAddress :</h2>
        <div className=" ">
          <form action={myFormik.handleSubmit}>
            <label htmlFor="details" className="d-block ">
              Details :{" "}
            </label>
            <textarea
              name="details"
              id=""
              cols="120"
              rows="2"
              className="mb-2 form-control"
              value={myFormik.values.details}
              onChange={myFormik.handleChange}
              onBlur={myFormik.handleBlur}
            >
              Details...
            </textarea>
            {myFormik.errors.details && myFormik.touched.details ? (
              <div className="alert alert-danger">{myFormik.errors.details}</div>
            ) : null}
            <label htmlFor="phone" className="d-block ">
              Phone :{" "}
            </label>
            <input
            id="phone"
              type="text"
              className="mb-2 form-control"
              placeholder="phone..."
              value={myFormik.values.phone}
              onChange={myFormik.handleChange}
              onBlur={myFormik.handleBlur}
            />
            {myFormik.errors.phone && myFormik.touched.phone ? (
              <div className="alert alert-danger">{myFormik.errors.phone}</div>
            ) : null}
            <label htmlFor="city" className="d-block ">
              City :{" "}
            </label>
            <input
            id="city"
              type="text"
              className="mb-2 form-control"
              placeholder="city..."
              value={myFormik.values.city}
              onChange={myFormik.handleChange}
              onBlur={myFormik.handleBlur}
            />
            <button type="submit" className="btn bg-main my-3">
              Confirm cash payment
            </button>
          </form>
        </div>
      </div>
    </>
  );
}
