import { useFormik } from "formik";
import React from "react";

export default function Register() {
const userData = {
  name:'',
  email:'',
  Password:"",
  rePassword:"",
  Phone:""
}
function mySubmit(values){
  console.log(values);
}
  const myFormik = useFormik({ 
  initialValues: userData,
  onSubmit: mySubmit,

  validate:function(values){
    const errors = {}
    const nameRegex =  /^[A-z][a-z]{3-7} $/ ;
    const phoneRegex = /^01[0125][0-9]{8} $/;
    if (nameRegex.test(values.name)=== false) {
      errors.name = "Name must be form 4 to 8 characters start with capital letter"
    }
    if (phoneRegex.test(values.Phone)!==true) {
      errors.phone ="Must use egyption namper"
    }
    if (values.email.includes("@") !== true || values.email.includes(".") !== true) {
      errors.email ="Email must be in format"
    }
    if (values.Password.length < 6|| values.Password.length > 12) {
      errors.Password ="password must be from 6 to 12 characters"
    }
    if (values.rePassword !== values.Password) {
      errors.rePassword ="password and repassword don't match"
    }
  }
  })





  return  <>
      <div className="w-75 m-auto p-5 ">
        <h3>Register Now :</h3>
        <form onSubmit={myFormik.handleSubmit}>
          <label htmlFor="name">name :</label>
          <input type="text" name="" id="name" onChange={myFormik.handleChange} value={myFormik.values.name} className="form-control mb-1" />
          <div className="alert alert-danger">{myFormik.errors.name}</div>

          <label htmlFor="email">email :</label>
          <input onChange={myFormik.handleChange} type="email" name="" id="email" value={myFormik.values.email}  className="form-control mb-1" />
          <label htmlFor="password">password :</label>
          <input onChange={myFormik.handleChange} type="password" name="" id="password" value={myFormik.values.Password} className="form-control mb-1" />
          <label htmlFor="rePassword">rePassword :</label>
          <input onChange={myFormik.handleChange} type="password" name="" id="rePassword" value={myFormik.values.rePassword} className="form-control mb-1" />
          <label htmlFor="phone">phone :</label>
          <input onChange={myFormik.handleChange} type="number" name="" id="phone" value={myFormik.values.Phone} className="form-control mb-1" />
          <button type="submit" className="btn bg-main mt-2 ms-auto d-flex text-white ">
            Register
          </button>
        </form>
      </div>
    </>
}
