import axios, { Axios } from "axios";
import React, { useEffect, useState } from "react";
import { RotatingLines } from "react-loader-spinner";
import { useQuery } from "react-query";
import SimpleSlider from "../HomeSlider/SimpleSlider";

export default function Products() {
  // const [allProduct, setallProduct] = useState(null);

  async function getAllProduct() {
    return await axios.get("https://ecommerce.routemisr.com/api/v1/products");
  }
  //     .then((res) => {
  //       setallProduct(res.data.data);
  //       // console.log(res.data.data);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  //   // console.log(data.data);
  // }
  // useEffect(() => {
  //   getAllProduct();
  // }, []);

  const { data, isLoading } = useQuery("getAllProducts", getAllProduct);
  if (isLoading) {
    return (
      <>
        <div className="vh-100 w-100 d-flex justify-content-center align-items-center bg-black">
          <RotatingLines
            visible={true}
            height="96"
            width="96"
            color="var(--main-color)"
            strokeWidth="5"
            animationDuration="0.75"
            ariaLabel="rotating-lines-loading"
            wrapperStyle={{}}
            wrapperClass=""
          />
        </div>
      </>
    );
  }

  return (
    <>
      <div className="container">
       <div className="row">
        <div className="col-9">
        <SimpleSlider/>
        </div>
        <div className="col-3">
          <figure><img src={require("")} alt="" /></figure>
        </div>
       </div>
        <div className="row gy-1">
          {data.data.data.map((product, index) => (
            <div key={index} className="col-md-2">
              <div className="product">
                <img className="w-100" src={product.imageCover} alt="product" />
                <h3 className="text-main h6">{product.category.name}</h3>
                <h2 className="h6">
                  {product.title.split(" ").slice(0, 2).join(" ")}
                </h2>
                <div className="d-flex justify-content-between">
                  {product.priceAfterDiscount ? (
                    <>
                      <p>
                        <span className="text-decoration-line-through">
                          {product.price}
                        </span>
                        -{product.priceAfterDiscount}
                        <span>EGP</span>
                      </p>
                    </>
                  ) : (
                    <p>
                      {product.price}
                      <span>EGP</span>
                    </p>
                  )}

                  <p>⭐{product.ratingsAverage}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      {/* {allProduct ? (
        <div className="container">
          <div className="row gy-1">
            {allProduct.map((product, index) => (
              <div key={index} className="col-md-2">
                <div className="product">
                  <img
