import React, { useContext } from "react";
import { addCart } from "../../Context/AddProductToCart";

export default function Cart() {
  const { getProductFromCart, numberOfCartItem, totalPrice, allProducts } =
    useContext(addCart);
  function getcart() {
    return getProductFromCart();
  }

  return (
    <>
      <div className="container">
        <div className="d-flex justify-content-between align-items-center">
          <h1>Shop cart:</h1>
          <button className="btn btn-outline-success">Delete Cart</button>
        </div>
        <div className="d-flex align-content-center align-items-center">
          <p className="text-main ">Total cart price :</p>
          <p className="text-main">{totalPrice} EGP</p>
        </div>

        <div className="d-flex">
          {allProducts?.map((productCart, index) => (
            <div key={index} className="row border-1 border-bottom border-black align-items-center py-3">
              <div className="col-md-2">
                <figure>
                  <img
                    className="w-100"
                    src={productCart.imageCover}
                    alt={productCart.title}
                  />
                </figure>
              </div>
              <div className="col-md-4">
                <p>{productCart.title}.</p>
                <p className="text-main">price :{productCart.products.price}</p>
                <p role="button" className="btn text-main ">
                  ðŸ—‘Remove
                </p>
              </div>
              <div className="col-md-2  d-flex justify-content-between align-items-center">
                <button className="btn btn-outline-success">+</button>
                <p>{productCart.products.count}</p>
                <button className="btn btn-outline-success">-</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
