import { useFormik } from "formik";
import React from "react";

export default function Payment() {
  const userData = {
    details: "",
    phone: "",
    city: "",
  };
  const my = useFormik({
    initialValues: userData,
    onSubmit: "",
    validate: (values) => {
      const errors = {};
      const phoneRegex = /^01[0125][0-9]{8}$/;
      if (!phoneRegex.test(values.phone)) {
        errors.phone = "Must use Egyptian phone number format";
      }
      if (values.details.length < 12) {
        errors.details = "Must be more than 12 characters";
      }
      return errors;
    },
  });

  return (
    <>
      <div className="container my-2 bg-light">
        <h2>ShippingAddress :</h2>
        <div className=" ">
          <form action={my.handleSubmit}>
            <label htmlFor="details" className="d-block ">
              Details :{" "}
            </label>
            <textarea
              name="details"
              id=""
              cols="120"
              rows="2"
              className="mb-2 form-control"
              value={my.values.details}
              onChange={my.handleChange}
              onBlur={my.handleBlur}
            >
              Details...
            </textarea>
            {myFormik.errors.name && myFormik.touched.name ? (
            <div className="alert alert-danger">{myFormik.errors.name}</div>
          ) : null}
            <label htmlFor="details" className="d-block ">
              Phone :{" "}
            </label>
            <input
              type="text"
              className="mb-2 form-control"
              placeholder="phone..."
              value={my.values.phone}
              onChange={my.handleChange}
              onBlur={my.handleBlur}
            />
            <label htmlFor="details" className="d-block ">
              City :{" "}
            </label>
            <input
              type="text"
              className="mb-2 form-control"
              placeholder="city..."
              value={my.values.city}
              onChange={my.handleChange}
              onBlur={my.handleBlur}
            />
            <button type="submit" className="btn bg-main my-3">Confirm</button>
          </form>
        </div>
      </div>
    </>
  );
}
