import axios from "axios";
import React, { useContext } from "react";
import { useQuery } from "react-query";
import { addCart } from "../../Context/AddProductToCart";
import { RotatingLines } from "react-loader-spinner";
import { Navigate } from "react-router-dom";

export default function AllOrders() {

const {userId}  = useContext(addCart)
localStorage.s

  async function getAllOrders() {
    return await axios.get(
      `https://ecommerce.routemisr.com/api/v1/orders/user/${userId}`
    );
  }
  const { data, isError, isLoading } = useQuery("getAllOrders", getAllOrders);

  if (isLoading) {
    return (
      <>
        <div className="vh-100 w-100 d-flex justify-content-center align-items-center bg-black">
          <RotatingLines
            visible={true}
            height="96"
            width="96"
            color="var(--main-color)"
            strokeWidth="5"
            animationDuration="0.75"
            ariaLabel="rotating-lines-loading"
            wrapperStyle={{}}
            wrapperClass=""
          />
        </div>
      </>
    );
  }
  if (isError) {
    return <Navigate to={"/login"} />;
  }
console.log(data?.data);
  return (
    <>
      <div className="container">
        <h2 className="text-main my-md-5">All Orders</h2>
        {data.data.map((orders , index)=>        <div key={index} className="row border-bottom border-light mb-3">
          <div className="col-md-2">
            <img className="w-100" src={orders.product.brand.image} alt="" />
          </div>
          <div className="col-6">
            <h5><span className="text-main ">TotalOrderPrice</span> : {orders.totalOrderPrice} </h5>
            <p><span className="text-main ">Details :</span> <br/> <span className="text-main ">Order to :</span>{orders.user.name} <br/> <span className="text-main ">phone :</span>{orders.user.phone} <br/> <span className="text-main ">Email</span> {orders.user.email}   </p>
          </div>
          <div className="col-md-2">{orders.paymentMethodType}</div>
        </div>)}

      </div>
    </>
  );
}
