import { useFormik } from 'formik'
import React from 'react'

export default function Payment() {
  const userData = {
    details : "" ,
    phone : "",
    city : ""
  }
const myFormic = useFormik({
  initialValues : userData,
  onSubmit : "",
  validate:(values)=>{
    const errors = {} ;
    const phoneRegex = /^01[0125][0-9]{8}$/;
    if (!phoneRegex.test(values.phone)) {
      errors.phone = "Must use Egyptian phone number format";
    }
    if (values.details.length < 12) {
      errors.details ="Must be more than 12 characters"
    }
  }
})


  return<>
  <div className='container'>
    <h2>ShippingAddress :</h2>
    <div className="w-50 my-auto">
    <form action={myFormic.handleSubmit}>
      <label htmlFor="details" className='d-block h2'>Details : </label>
      <textarea name="details" id="" cols="120" rows="2" className='my-1'>Details...</textarea>
      <label htmlFor="details" className='d-block '>Details : </label>
    </form>
    </div>
  </div>
  </>
}
