import axios, { Axios } from "axios";
import React, { useEffect, useState } from "react";

export default function Products() {
  const [allProduct, setallProduct] = useState([])

  async function getAllProduct() {
    const {data}= await axios.get("https://ecommerce.routemisr.com/api/v1/products").then((res)=>{
      setallProduct(res.data.data)
    }).catch((err)=>{
      console.log(err);
    })
    // console.log(data.data);
    
  }
useEffect(()=>{
  getAllProduct()
},[])
  return (
    <>
      <div className="container">
        <div className="row">
          {allProduct.map((product,index)=>{})}
          <div className="col-md-2">
            <div className="product">
              <img
                className="w-100"
                src={require("../../assets/finalProject assets/1678303526206-cover.jpeg")}
                alt="product"
              />
              <h6 className="text-main">{allProduct.category.name}</h6>
              <h5>{allProduct.title}</h5>
              <div className="d-flex justify-content-between">
                <p>
                {allProduct.price} <span>EGP</span>
                </p>
                <p>‚≠ê{allProduct.ratingsAverage}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
