import axios, { Axios } from "axios";
import React, { useEffect, useState } from "react";

export default function Products() {
  const [allProduct, setallProduct] = useState([]);

  async function getAllProduct() {
    await axios
      .get("https://ecommerce.routemisr.com/api/v1/products")
      .then((res) => {
        setallProduct(res.data.data);
        console.log(res.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
    // console.log(data.data);
  }
  useEffect(() => {
    getAllProduct();
  }, []);
  return (
    <>
    <div className="vh-100 d-flex justify-content-center"></div>
      <div className="container">
        <div className="row">
          {allProduct.map((product, index) => (
            <div key={index} className="col-md-2">
              <div className="product">
                <img className="w-100" src={product.imageCover} alt="product" />
                <h6 className="text-main">{product.category.name}</h6>
                <h5>{product.title}</h5>
                <div className="d-flex justify-content-between">
                  <p>
                    {product.price} <span>EGP</span>
                  </p>
                  <p>‚≠ê{product.ratingsAverage}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
