import axios, { Axios } from "axios";
import React, { useEffect, useState } from "react";
import { RotatingLines } from "react-loader-spinner";

export default function Products() {
  const [allProduct, setallProduct] = useState(null);

  async function getAllProduct() {
    await axios
      .get("https://ecommerce.routemisr.com/api/v1/products")
      .then((res) => {
        setallProduct(res.data.data);
        console.log(res.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
    // console.log(data.data);
  }
  useEffect(() => {
    getAllProduct();
  }, []);
  return (
    <>
    {allProduct?      <div className="container">
        <div className="row">
          {allProduct.map((product, index) => (
            <div key={index} className="col-md-2">
              <div className="product">
                <img className="w-100" src={product.imageCover} alt="product" />
                <h3 className="text-main h6">{product.category.name}</h3>
                <h2 className="h5">{product.title}</h2>
                <div className="d-flex justify-content-between">
                  
                  <p>
                    <span>{product.price}</span>-{product.priceAfterDiscount} <span>EGP</span>
                  </p>
                  <p>‚≠ê{product.ratingsAverage}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div> :     <div className="vh-100 w-100 d-flex justify-content-center align-items-center bg-black">
    <RotatingLines
  visible={true}
  height="96"
  width="96"
  color="var(--main-color)"
  strokeWidth="5"
  animationDuration="0.75"
  ariaLabel="rotating-lines-loading"
  wrapperStyle={{}}
  wrapperClass=""
  />
    </div>}


    </>
  );
}
